package com.class17_2;

public class Patter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1; i<=7; i++) {
			for(int y=1; y<=7; y++) {
				System.out.print(i);
			}
			System.out.println();
		}

		
		for(int i=4; i>0; i--) {
			for(int y=5; y>0; y--) {
				System.out.print(y);
			}
			System.out.println();
		}
		for(int i=5; i>0; i--) {
			for(int y=5; y>0; y--) {
				System.out.print(i);
			}
			System.out.println();
		}
		
	}
	

}
