package com.class7;

public class CompoundOPerators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10;
		a=a+10;
		System.out.println(a);
		
		int b=100;
		b=b+100;
		System.out.println(b);
		
		int c=100;
		c+=100;
		
		System.out.println(c);
		
		int d=30;
		
		d-=10;
		System.out.println(d);
		
		d+=50;
		
		System.out.println(d);
		
		d/=5;
		
		System.out.println(d);
		
		d%=2;
		
		System.out.println(d);
	}

}
